package net.sf.jclec.exprtree.fun;

/**
 * Argument. This class represents generically an argument in the arguments pool.
 * 
 * @author Sebastian Ventura
 *
 * @param <E> Argument type
 */

public abstract class Argument<E> extends AbstractPrimitive 
{
	/////////////////////////////////////////////////////////////////
	// --------------------------------------- Serialization constant
	/////////////////////////////////////////////////////////////////
	
	/** Generated by Eclipse */
	
	private static final long serialVersionUID = 9098840243430421165L;
	
	/////////////////////////////////////////////////////////////////
	// --------------------------------------------------- Properties
	/////////////////////////////////////////////////////////////////

	/** Argument index */
	
	protected int argindex;
	
	/////////////////////////////////////////////////////////////////
	// ------------------------------------------------- Constructors
	/////////////////////////////////////////////////////////////////

	public Argument(Class<E> rtype, int argindex) 
	{
		// Call super constructor
		super(new Class<?> [0], rtype);
		// Set argument index
		this.argindex = argindex;
	}

	/////////////////////////////////////////////////////////////////
	// ------------------------------------------------- Constructors
	/////////////////////////////////////////////////////////////////

	@Override
	protected final void evaluate(ExprTreeFunction context) 
	{
		pushArg(context, argindex);
	}
}
