package net.sf.jclec.realarray.rec;


/**
 * Algebraic Connectives
 * 
 * @author Alberto Lamarca-Rosales 
 * @author Sebastian Ventura
 */

public class AlgebraicConnectives extends AbstractFuzzyConnectives 
{
	/////////////////////////////////////////////////////////////////
	// --------------------------------------- Serialization constant
	/////////////////////////////////////////////////////////////////
	
	/** Generated by eclipse */

	private static final long serialVersionUID = -7834868496958746505L;

	/////////////////////////////////////////////////////////////////
	// ------------------------------------------------- Constructors
	/////////////////////////////////////////////////////////////////
	
	/**
	 * Empty constructor
	 */
	
	public AlgebraicConnectives() 
	{
		super();
	}

	/////////////////////////////////////////////////////////////////
	// ---------------------------------- AbstractConnectives methods
	/////////////////////////////////////////////////////////////////

	/**
	 * Algebraic Function m
	 */
	
	public double m(double x, double y) 
	{
		 return Math.pow(x,(1-alpha))*Math.pow(y,alpha);
	}
	
	/**
	 * Algebraic Function s
	 */
	
	public double s(double x, double y) 
	{
		return (x + y - x*y );
	}
	
	/**
	 * Algebraic Function f
	 */
	
	public double f(double x, double y) 
	{
		return (x*y);
	}
	
	/**
	 * Algebraic Function l
	 */
	
	public double l(double x, double y) 
	{
		return m(f(x,y),s(x,y));
	}
}
